links.css:
  @charset "UTF-8";

/* Overlay */
  .redir-modal-bg {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    background: url(https://images-na.ssl-images-amazon.com/images/G/01/x-locale/paladin/1x1-000-50._V362364097_.png) repeat;
    background: #000000;
    z-index: 1000;
    opacity: .5;
    -ms-filter:"progid:DXImageTransform.Microsoft.Alpha(Opacity=50)";
    filter: alpha(opacity=50);
  }

  #redir-overlay {
    position: absolute;
    top: 39%;
    left: 50%;
    width: 529px;
    margin: -145px 0 0 -263px;

    background-color: #FFF;
    -webkit-border-radius: 5px;
    -moz-border-radius: 5px;
    border-radius: 5px;
    font-family: Helvetica Neue, Arial !important;
    line-height: 1.35;
    overflow: hidden;
    padding: 0;
    z-index: 1001;
  }

  .redir-title {
    background-image: url(https://images-na.ssl-images-amazon.com/images/G/01/x-locale/paladin/pop/beacon_header._V362704570_.png);
    font-size: 13px;
    font-weight: bold;
    line-height: 16px;
    padding: 10px 14px 9px;
    border-bottom: 1px solid #ccc;
  }

  .redir-content {
    padding: 14px 14px;
  }

  #redir-footer {
    background-image: url(https://images-na.ssl-images-amazon.com/images/G/01/x-locale/paladin/pop/beacon_footer._V362704544_.png);
    background-repeat: repeat-x;
    min-height: 33px;
    text-align: right;
    padding: 14px;
    box-sizing: content-box;
    -moz-box-sizing: content-box;
  }

  #redir-overlay #redir-remember {
    color: #333333;
    font-size: 21px;
    line-height: 27px;
    padding: 2px;
  }

  #redir-opt-out {
    width: 13px;
    height: 13px;
    padding: 0;
    margin: 0;
    vertical-align: bottom;
    position: relative;
    top: -2px;
    overflow: hidden;
  }

  #redir-opt-out-label {
    display: block;
    padding-left: 15px;
    text-indent: -15px;
    margin-top: 1px;
    margin-bottom: 10px;
    font-weight: normal;
  }

  #redir-overlay #redir-amazon-donates, #redir-overlay #redir-reminder-count {
    font-size: 13px;
    padding-left: 2px;
    line-height: 19px;
  }

  #redir-overlay #redir-amazon-donates {
    color: #888888;
  }

  #redir-overlay #redir-reminder-count {
    color: #333333;
    margin-top: 20px;
  }

  .redir-title-text {
    float: left;
  }

  .redir-clear {
    clear: both;
  }

  .redir-dismiss-x {
    font-size: 13px;
    float: right;
  }

  .redir-dismiss-x a:hover {
    text-decoration: none;
  }

  .redir-overlay-close {
    background-position: -297px -5px;
    height: 9px;
